const mysignal = "";
const a = "a".charCodeAt(0);
let signal, len, win, mark, bin, dup, x, dm;

function reset() {
    len = 14;
    win = 0;
    mark = 0;
    bin = new Array(26).fill(0);
    dup = 0;
    x = 795;
    dm = 0;
}

function go() {
    reset();
    len = parseInt(select("#len").value());
    signal = select("#signal").value();
}

function setup() {
    frameRate(60);
    createCanvas(800, 400);
    reset();
    signal = mysignal;
    select("#len").value(len);
    select("#signal").value(signal);
    select("#go").mouseClicked(go);
}

function draw() {
    background(51);

    textSize(24);
    textAlign(LEFT, TOP);
    noStroke();
    fill(0, 255, 0);
    text("Start-of-message = " + mark, 15, 15);

    strokeWeight(1);
    stroke(0, 153, 0);
    noFill();
    for (let i = 0; i < win; ++i) {
        rect(11 + (6 + i) * 30, 360, 28, 28);
    }

    textSize(28);
    textAlign(CENTER, CENTER);
    for (let i = 0; i < 26; ++i) {
        strokeWeight(1);
        stroke(0, 102, 0);
        fill(0, 153, 0);
        text(String.fromCharCode(a + i), 25 + i * 30, 320);

        strokeWeight(3);
        stroke(0, 153, 0);
        if (bin[i] > 0) {
            fill(0, 204, 0);
            for (let j = 0; j < bin[i]; ++j) {
                rect(15 + i * 30, 280 - j * 30, 20, 20);
            }
        } else {
            noFill();
            rect(15 + i * 30, 280, 20, 20);
        }
    }

    if (x >= 205) {
        textSize(20);
        noStroke();
        fill(0, 255, 0);
        for (let i = 0; i < 20 && i < signal.length; ++i) {
            text(signal.charAt(i), x + i * 30, 374);
        }
        x -= 5;
        if (x < 205) {
            frameRate(3);
        }
    } else if (win < len) {
        textSize(20);
        noStroke();
        fill(0, 255, 0);
        for (let i = 0; i < 20 && i < signal.length; ++i) {
            text(signal.charAt(i), 205 + i * 30, 374);
        }
        const c = signal.charCodeAt(mark) - a;
        if (++bin[c] == 2) {
            ++dup;
        }
        ++mark;
        ++win;
        if (win == len) {
            frameRate(60);
        }
    } else if (dup > 0) {
        if (x == 200) {
            x = 205;
        }
        if (x >= 30) {
            x -= 30;
        } else {
            dm++;
        }
        textSize(20);
        noStroke();
        fill(0, 255, 0);
        for (let i = dm; i < dm + 26 && i < signal.length; ++i) {
            text(signal.charAt(i), x + (i - dm) * 30, 374);
        }
        let c = signal.charCodeAt(mark) - a;
        if (++bin[c] == 2) {
            ++dup;
        }
        c = signal.charCodeAt(mark - len) - a;
        if (--bin[c] == 1) {
            --dup;
        }
        ++mark;
    } else {
        textSize(20);
        noStroke();
        fill(0, 255, 0);
        for (let i = dm; i < dm + 26 && i < signal.length; ++i) {
            text(signal.charAt(i), x + (i - dm) * 30, 374);
        }
    }
}
